													show:		((curFrame1 > 0) ? !(show) : show)}

												: {	curFrame1:	((curFrame2 > 0) ? (curFrame2 - 1) : curFrame2),
													show:		((curFrame2 > 0) ? !(show) : show)}

'NextFrameBtnClicked'; show	? {	curFrame2:	((curFrame1 < tutorial.frames.length-1) ? (curFrame1 + 1) : curFrame1),
													show:		((curFrame1 < tutorial.frames.length-1) ? !(show) : show)}

												: {	curFrame1:	((curFrame2 < tutorial.frames.length-1) ? (curFrame2 + 1) : curFrame2),
													show:		((curFrame2 < tutorial.frames.length-1) ? !(show) : show)}

'NextFrameBtnClicked';	((curFrame1 == maxAvailableFrame) || (curFrame1 == maxAvailableFrame)) ? 'unlockNextFrame' : null; null; 0;
											(tutorial.frames[maxAvailableFrame].nextFrameDelay != null	? (tutorial.frames[maxAvailableFrame].nextFrameDelay + windowAppearLag)
																										: tutorial.config.defaultNextFrameDelay)

